        </h3>
        <p className="text-gray-700 mb-3">{tagline}</p>
        {/* Pillars Icons */}
        <div className="flex space-x-3 mb-4" aria-label="Program pillars">
          {pillars.map((pillar) => (
            <div key={pillar} title={pillar} aria-hidden="true">
              {pillarIcons[pillar] || null}
